// Gerar dados do relat贸rio mensal
const items = $input.all();

if (!items || items.length === 0) {
  console.log('Nenhuma empresa ativa encontrada');
  return [];
}

const mesPassado = new Date();
mesPassado.setMonth(mesPassado.getMonth() - 1);
const mesReferencia = mesPassado.toLocaleDateString('pt-BR', { year: 'numeric', month: 'long' });
const anoMes = mesPassado.toISOString().slice(0, 7);

const relatoriosGerados = [];

for (const item of items) {
  try {
    const empresa = item.json;

    // Validar dados essenciais
    if (!empresa.id || !empresa.nome) {
      console.warn('Empresa com dados incompletos:', empresa.id);
      continue;
    }

    // Simular dados do relat贸rio (em produ莽茫o, viria do banco)
    const faturamentoMes = Math.random() * 50000 + 10000;
    const dasValor = faturamentoMes * 0.06;
    const aliquotaEfetiva = 6.0 + (Math.random() * 2);

    // Personalizar por regime tribut谩rio
    let dadosEspecificos = {};
    let proximosVencimentos = [];

    switch(empresa.regime_tributario) {
      case 'Simples Nacional':
        dadosEspecificos = {
          faturamento_mes: faturamentoMes,
          das_valor: dasValor,
          aliquota_efetiva: aliquotaEfetiva,
          anexo_simples: 'Anexo I',
          faturamento_acumulado: faturamentoMes * 12 * 0.8
        };
        proximosVencimentos = [
          { tipo: 'DAS', vencimento: '20/02/2025', valor: dasValor },
          { tipo: 'DEFIS', vencimento: '31/03/2025', valor: 0 }
        ];
        break;

      case 'Lucro Presumido':
        const receitaTrimestre = faturamentoMes * 3;
        const irpj = receitaTrimestre * 0.08 * 0.15;
        const csll = receitaTrimestre * 0.08 * 0.09;

        dadosEspecificos = {
          receita_trimestre: receitaTrimestre,
          irpj: irpj,
          csll: csll,
          total_tributos: irpj + csll,
          percentual_presuncao: 8.0
        };
        proximosVencimentos = [
          { tipo: 'IRPJ', vencimento: '31/01/2025', valor: irpj },
          { tipo: 'CSLL', vencimento: '31/01/2025', valor: csll }
        ];
        break;

      default:
        dadosEspecificos = {
          faturamento_mes: faturamentoMes,
          observacao: `Relat贸rio personalizado para ${empresa.regime_tributario} em desenvolvimento`
        };
        proximosVencimentos = [
          { tipo: 'Consultar', vencimento: 'A definir', valor: 0 }
        ];
    }

    const relatorio = {
      ...empresa,
      relatorio: {
        mes_referencia: mesReferencia,
        ano_mes: anoMes,
        ...dadosEspecificos,
        proximos_vencimentos: proximosVencimentos,
        gerado_em: new Date().toISOString(),
        destinatario: empresa.email_contato || empresa.contador_email || 'email@exemplo.com'
      }
    };

    relatoriosGerados.push({ json: relatorio });

  } catch (error) {
    console.error('Erro ao gerar relat贸rio para empresa:', error.message);
    continue;
  }
}

console.log(`Gerados ${relatoriosGerados.length} relat贸rios`);
return relatoriosGerados;
