// Processar alertas fiscais
const items = $input.all();
const hoje = new Date();
const alertasProcessados = [];

for (const item of items) {
  const prazo = item.json;
  const dataVencimento = new Date(prazo.due_date);
  const diasRestantes = Math.ceil((dataVencimento - hoje) / (1000 * 60 * 60 * 24));

  // Classificar urg√™ncia
  let urgencia = 'baixa';
  let emoji = 'üìÖ';
  let cor = '#28a745'; // verde

  if (diasRestantes <= 1) {
    urgencia = 'critica';
    emoji = 'üö®';
    cor = '#dc3545'; // vermelho
  } else if (diasRestantes <= 3) {
    urgencia = 'alta';
    emoji = '‚ö†Ô∏è';
    cor = '#fd7e14'; // laranja
  } else if (diasRestantes <= 7) {
    urgencia = 'media';
    emoji = 'üìã';
    cor = '#ffc107'; // amarelo
  }

  // Criar mensagem personalizada
  const titulo = `${emoji} ${urgencia.toUpperCase()}: ${prazo.name}`;
  const corpo = `Empresa: ${prazo.empresa_nome} (${prazo.cnpj})\nVencimento: ${dataVencimento.toLocaleDateString('pt-BR')} (${diasRestantes} dias)\nValor: R$ ${(prazo.estimated_amount || 0).toLocaleString('pt-BR', {minimumFractionDigits: 2})}\nTipo: ${prazo.obligation_type}`;

  alertasProcessados.push({
    json: {
      ...prazo,
      urgencia,
      diasRestantes,
      titulo,
      corpo,
      cor,
      emoji,
      dataFormatada: dataVencimento.toLocaleDateString('pt-BR'),
      valorFormatado: (prazo.estimated_amount || 0).toLocaleString('pt-BR', {minimumFractionDigits: 2})
    }
  });
}

return alertasProcessados;
